<template>  <div>    <el-breadcrumb separator-class="el-icon-arrow-right" style="font-size: 16px;">      <el-breadcrumb-item :to="{ path: '/sys-admin' }">        <i class="el-icon-s-promotion"></i> 后台管理      </el-breadcrumb-item>      <el-breadcrumb-item>相册列表</el-breadcrumb-item>    </el-breadcrumb>    <el-divider></el-divider>    <!--至少一列不设置宽度-->    <el-table :data="tableData" border style="width: 100%">      <el-table-column prop="id" label="ID" width="100"></el-table-column>      <el-table-column prop="name" label="相册名称"></el-table-column>      <el-table-column prop="description" label="相册描述"></el-table-column>      <el-table-column prop="sort" label="自定义排序序号" width="150" align="center"></el-table-column>      <el-table-column prop="createTime" label="创建时间" width="150" align="center"></el-table-column>      <el-table-column prop="updateTime" label="更新时间" width="150" align="center"></el-table-column>      <el-table-column label="操作" width="120" align="center">        <template>          <el-button type="primary" size="small" icon="el-icon-edit"                     circle @click=""></el-button>          <el-button type="danger" size="small" icon="el-icon-delete"                     circle @click="openDeleteConfirm()"></el-button>        </template>      </el-table-column>    </el-table>  </div></template><script>export default {  data() {    return {      tableData: [],    }  },  methods: {    loadAlbumList() {      console.log('loadAlbumList')      let url = "http://localhost:9080/albums";      console.log("url:", url);      this.axios.get(url).then((response) => {        let responseBody = response.data;        this.tableData = responseBody.data;      })    },    // 删除相册    handleDelete() {      console.log('准备删除相册')    },    openDeleteConfirm() {      this.$confirm('此操作将永久删除该相册, 是否继续?', '提示', {        confirmButtonText: '确定',        cancelButtonText: '取消',        type: 'warning'      }).then(() => {        this.handleDelete();        this.$message({          type: 'success',          message: '删除成功!'        });      }).catch(() => {        // this.$message({        //   type: 'info',        //   message: '已取消删除'        // });      });    },  },  mounted() {    console.log('AlbumListView mounted');    this.loadAlbumList();  }}</script>