<template>  <div>    <el-breadcrumb separator-class="el-icon-arrow-right" style="font-size: 16px;">      <el-breadcrumb-item :to="{ path: '/sys-admin' }">        <i class="el-icon-s-promotion"></i> 后台管理      </el-breadcrumb-item>      <el-breadcrumb-item>相册列表</el-breadcrumb-item>    </el-breadcrumb>    <el-divider></el-divider>    <!--至少一列不设置宽度-->    <el-table :data="tableData" border style="width: 100%">      <el-table-column prop="id" label="ID" width="100"></el-table-column>      <el-table-column prop="name" label="相册名称"></el-table-column>      <el-table-column prop="description" label="相册描述"></el-table-column>      <el-table-column prop="sort" label="自定义排序序号" width="150" align="center"></el-table-column>      <el-table-column prop="createTime" label="创建时间" width="150" align="center"></el-table-column>      <el-table-column prop="updateTime" label="更新时间" width="150" align="center"></el-table-column>      <el-table-column label="操作" width="120" align="center">        <template>          <el-button type="primary" size="small" icon="el-icon-edit" circle></el-button>          <el-button type="danger" size="small" icon="el-icon-delete" circle></el-button>        </template>      </el-table-column>    </el-table>  </div></template><script>export default {  data() {    return {      tableData: [],    }  },  methods: {    loadAlbumList() {      console.log('loadAlbumList')      let url = "http://localhost:9080/albums";      console.log("url:", url);      this.axios.get(url).then((response) => {        let responseBody = response.data;        this.tableData = responseBody.data;      })    }  },  mounted() {    console.log('AlbumListView mounted');    this.loadAlbumList();  }}</script>