<template>  <div>    <el-breadcrumb separator-class="el-icon-arrow-right" style="font-size: 16px;">      <el-breadcrumb-item :to="{ path: '/sys-admin' }">        <i class="el-icon-s-promotion"></i> 后台管理      </el-breadcrumb-item>      <el-breadcrumb-item>相册列表</el-breadcrumb-item>    </el-breadcrumb>    <el-divider></el-divider>    <!--至少一列不设置宽度-->    <el-table :data="tableData" border style="width: 100%">      <el-table-column prop="id" label="ID" width="100"></el-table-column>      <el-table-column prop="name" label="相册名称"></el-table-column>      <el-table-column prop="description" label="相册描述"></el-table-column>      <el-table-column prop="sort" label="自定义排序序号" width="150" align="center"></el-table-column>      <el-table-column prop="createTime" label="创建时间" width="150" align="center"></el-table-column>      <el-table-column prop="updateTime" label="更新时间" width="150" align="center"></el-table-column>      <el-table-column label="操作" width="120" align="center">        <template slot-scope="scope">          <el-button type="primary" size="small" icon="el-icon-edit"                     circle @click="handleEdit(scope.row)"></el-button>          <el-button type="danger" size="small" icon="el-icon-delete"                     circle @click="openDeleteConfirm(scope.row)"></el-button>          <!--                                        scope.row 为当前行数据-->        </template>      </el-table-column>    </el-table>  </div></template><script>export default {  data() {    return {      tableData: [],    }  },  methods: {    // 发请求获取相册列表    loadAlbumList() {      console.log('loadAlbumList')      let url = "http://localhost:9080/albums";      console.log("url:", url);      this.axios.get(url).then((response) => {        let responseBody = response.data;        this.tableData = responseBody.data;      })    },    handleEdit(album) {      let message = '您正在尝试编辑【' + album.id + '-' + album.name + '】的相册详情，抱歉，该功能尚未实现……';      this.$alert(message, '提示', {            confirmButtonText: '确定'          });    },    // 删除相册    handleDelete(album) {      console.log('准备删除相册,id=', album.id)      let url = "http://localhost:9080/albums/" + album.id + "/delete";      console.log("url:", url);      this.axios.post(url).then((response) => {        console.log(response);        let responseBody = response.data;        if (responseBody.state != 20000) {          this.$message.error(responseBody.message);        }        if (responseBody.state == 20000 || responseBody.state == 40400) {          this.loadAlbumList();        }      });    },    openDeleteConfirm(album) {      let message = '此操作将永久删除相册:【' + album.name + '】, 是否继续?';      this.$confirm(message, '提示', {        confirmButtonText: '确定',        cancelButtonText: '取消',        type: 'warning'      }).then(() => {        this.handleDelete(album);        this.$message({          type: 'success',          message: '删除成功!'        });      }).catch(() => {        // this.$message({        //   type: 'info',        //   message: '已取消删除'        // });      });    },  },  mounted() {    console.log('AlbumListView mounted');    this.loadAlbumList();  }}</script>